mapper.select:
SELECT id, title, author, genre, publication_date, price, stock_quantity, created_at, updated_at
FROM book
WHERE id = #{id}

SELECT id, title, author, genre, publication_date, price, stock_quantity, created_at, updated_at
FROM book
WHERE id = #{id}

SELECT id, title, author, genre, publication_date, price, stock_quantity, created_at, updated_at
FROM book


        AND title LIKE CONCAT('%', #{title}, '%')


        AND author LIKE CONCAT('%', #{author}, '%')


        AND genre LIKE CONCAT('%', #{genre}, '%')


        AND price >= #{minPrice}


        AND price <= #{maxPrice}


        AND stock_quantity >= #{minStock}


        AND stock_quantity <= #{maxStock}


ORDER BY id DESC

mapper.update:
UPDATE book
SET stock_quantity = stock_quantity - #{quantity},
    updated_at = NOW()
WHERE id = #{bookId}
  AND stock_quantity >= #{quantity}

mapper.select:
SELECT COUNT(*) > 0
FROM book
WHERE id = #{bookId}
  AND stock_quantity >= #{quantity}

SELECT stock_quantity
FROM book
WHERE id = #{bookId}
