SELECT id, title, author, genre, publication_date, price, stock_quantity, created_at, updated_at
FROM book


        AND title LIKE CONCAT('%', #{criteria.title}, '%')


        AND author LIKE CONCAT('%', #{criteria.author}, '%')


        AND genre LIKE CONCAT('%', #{criteria.genre}, '%')


        AND price >= #{criteria.minPrice}


        AND price <= #{criteria.maxPrice}


        AND publication_date >= #{criteria.startDate}


        AND publication_date <= #{criteria.endDate}


ORDER BY id DESC

    LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE book
SET stock_quantity = stock_quantity - #{quantity},
updated_at = NOW()
WHERE id = #{bookId}
AND stock_quantity >= #{quantity}

mapper.select:
SELECT COUNT(*) > 0
FROM book
WHERE id = #{bookId}
AND stock_quantity >= #{quantity}

SELECT stock_quantity
FROM book
WHERE id = #{bookId}
